import React, { useEffect } from "react";
import { Button } from 'semantic-ui-react'
import { useParams } from "react-router-dom";

function DeleteCardButton() {
    const button = <Button secondary onClick={handleClick} style={{background: "#BF0000"}}>Delete Log</Button>

    let { id } = useParams;
    console.log(id)
    useEffect(() => {
        fetch(`http://localhost:3000/reflections/${id}`)
            .then((res) => res.json())
            .then((data) => console.log(data) )
    }, [id])

    function handleClick(id){
       const confirm= window.confirm("This log will be deleted.");

       if (confirm) {
            deleteCard(id);
       } else {
            console.log("Log not deleted")
       }
       console.log(confirm);
    }

    function deleteCard(id){
        fetch(`http://localhost:3000/reflections/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json"
            }
        })
        .then(res => res.json())
        .then(data => console.log(data))
        }


    return(
        <div>
            {button}
            <br />
            <br />
        </div>
    )

    }

export default DeleteCardButton;
